-- Table `gt_hotel`.`t_room_category`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `gt_hotel`.`t_room_category` (
  `id`                  INT         NOT NULL AUTO_INCREMENT,
  `name`                VARCHAR(45) NOT NULL
  COMMENT '房型名称',
  `room_count`          INT         NOT NULL DEFAULT 0
  COMMENT '房间总数',
  `hotel_id`            INT         NOT NULL
