  COMMENT '是否开启早餐券 0 开启 1 关闭',
  `breakfast_quantity`  TINYINT(2)  NOT NULL DEFAULT 0
  COMMENT '早餐数量',
  `created_by`          INT         NOT NULL
  COMMENT '创建者ID',
