  COMMENT '创建时间',
  `updated_by`          INT         NOT NULL
  COMMENT '最后修改人 ID',
  `updated_at`          TIMESTAMP   NOT NULL
  COMMENT '最后修改时间',
  PRIMARY KEY (`id`),
  UNIQUE INDEX `name_UNIQUE` (`name` ASC)
)
  ENGINE = InnoDB
  COMMENT = '房型';

