-- Table `gt_hotel`.`t_hotel_setting`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `gt_hotel`.`t_hotel_setting` (
  `hotel_id`                     INT          NOT NULL
  COMMENT '酒店ID',
  `pay_mode`                     INT(1)       NOT NULL DEFAULT 0
  COMMENT '支付方式(1：在线支付 | 2：到店支付 | 3：1&2)  默认 在线支付',
  `sms_enable`                   INT(1)       NOT NULL DEFAULT 1
  COMMENT '是否开启短信通知 默认 1 不开启 0 开启',
  `sms_phone`                    CHAR(15)     NULL
  COMMENT '接收短信的手机号',
  `notice_enable`                INT(1)       NOT NULL DEFAULT 1
  COMMENT '酒店公告 1 不开启 0 开启',
  `bulletin`                     VARCHAR(300) NULL
  COMMENT '公告',
  `remnant_room_enable`          INT(1)       NOT NULL DEFAULT 1
  COMMENT '是否显示剩余房间数 默认 1 关闭 0 开启',
  `food_pay_mode`                INT(1)       NOT NULL DEFAULT 1
  COMMENT '餐饮支付方式 1 在线 2 到付 3 在线&到付',
  `reservation_check_out_enable` INT(1)       NOT NULL DEFAULT 1
  COMMENT '预约退房 0 开启 1 关闭  默认1',
  `reservation_check_out_phone`  CHAR(15)     NULL
  COMMENT '预约退房 接收短信的手机号',
  `Invoice_category`             CHAR(20)     NULL
  COMMENT '发票支持的类目 1 办公用品 2 住宿费 3 餐费 4 培训费 5 打球费 6 健身费 存储方式 1,2,3,4,5 or 1,2,3',
  `created_by`                   INT          NOT NULL
  COMMENT '创建者ID',
  `created_at`                   DATETIME     NOT NULL
  COMMENT '创建时间',
  `updated_by`                   INT          NOT NULL
  COMMENT '最后修改人 ID',
  `updated_at`                   TIMESTAMP    NOT NULL
  COMMENT '最后修改时间',
  PRIMARY KEY (`hotel_id`)
)
  ENGINE = InnoDB
  COMMENT = '酒店移动端设置';

