-- -----------------------------------------------------
-- Table `gt_hotel`.`t_activity`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `gt_hotel`.`t_activity` (
  `id`                 INT                 NOT NULL AUTO_INCREMENT,
  `hotel_id`           INT                 NOT NULL
  COMMENT '酒店ID',
  `external_number`    CHAR(16)            NOT NULL
  COMMENT '外部活动编号 提供对外显示 ID 格式 前缀 HD+时间戳年月日时分秒毫秒 14位',
  `begin_time`         DATETIME            NOT NULL
  COMMENT '活动 开始时间',
  `end_time`           DATETIME            NOT NULL
  COMMENT '结束时间',
  `available_time`     DATETIME            NOT NULL
  COMMENT '可入住最大时间 活动开始后，预订入住时间 最大有效期。',
  `restriction_enable` TINYINT(1)          NOT NULL DEFAULT 1
  COMMENT '是否开启限购 0 — 开启  1 不限制  默认1',
  `restriction_number` TINYINT(3) UNSIGNED NULL
  COMMENT '限购数量 当限购开启 - 限购最少限购1间',
  `show_room_enable`   TINYINT(1)          NOT NULL DEFAULT 1
  COMMENT '是否开启显示房间数 0 是  1 不是 默认 1',
  `number_threshold`   TINYINT(3)          NULL
  COMMENT '显示剩余房间数的临界值 当show_room_enable=0 此值不能为空。最大999',
  `activity_rules`     TEXT(500)           NOT NULL
  COMMENT '活动规则',
  `publish_status`     TINYINT(1)          NOT NULL DEFAULT 0
  COMMENT '活动发布状态 0 开启 1 未开启 ',
  `mark_modified`      TINYINT(1)          NOT NULL DEFAULT 0
  COMMENT '0 正常 1 禁用 2 删除',
  `created_by`         INT                 NOT NULL
  COMMENT '创建者ID',
  `created_at`         DATETIME            NOT NULL
  COMMENT '创建时间',
  `updated_by`         INT                 NOT NULL
  COMMENT '最后修改人 ID',
  `updated_at`         TIMESTAMP           NOT NULL
  COMMENT '最后修改时间',
  PRIMARY KEY (`id`),
  UNIQUE INDEX `external_number_UNIQUE` (`external_number` ASC)
)
  ENGINE = InnoDB
  COMMENT = '活动设置 - 抽出活动公共设置';

