  `id` INT NOT NULL AUTO_INCREMENT COMMENT '文件ID',
  `name` VARCHAR(60) NOT NULL COMMENT '显示文件名称',
  `original_name` VARCHAR(60) NOT NULL COMMENT '文件上传时的原始名称',
  `path` VARCHAR(300) NOT NULL COMMENT '文件路径 例如：/aa/bb/cc/f.txt',
