-- -----------------------------------------------------
-- Table `gt_hotel`.`t_order`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `gt_hotel`.`t_order` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `order_num` CHAR(25) NOT NULL COMMENT '外部订单编号 提供对外显示 ID 格式 前缀 DD+时间戳年月日时分秒毫秒 14位',
  `hotel_id` INT NOT NULL COMMENT '酒店ID',
  `bus_id` INT NOT NULL COMMENT '商家ID',
  `member_id` INT NULL COMMENT '会员ID',
  `pay_type` TINYINT(2) NOT NULL COMMENT '支付类型 =1 在线支付 =2 到店支付 =3 储值卡支付 =4 信用卡 =5 现金',
  `pay_status` TINYINT(2) NOT NULL DEFAULT 0 COMMENT '支付状态 =0 待支付 =1 已支付 =2 退款中 =3 已退款 默认 0',
  `pay_time` DATETIME NULL COMMENT '支付时间',
  `order_status` TINYINT(2) NOT NULL DEFAULT 0 COMMENT '订单流程状态 =0 处理中 =1 已确认 =2 已取消 =3 已完成 默认0',
  `create_time` DATETIME NOT NULL COMMENT '创建订单时间',
  `real_price` INT UNSIGNED NOT NULL DEFAULT 0 COMMENT '实收金额',
  `bill_price` INT UNSIGNED NOT NULL DEFAULT 0 COMMENT '账单金额(入住期间消费的金额统计)',
  `fb` INT UNSIGNED NOT NULL DEFAULT 0 COMMENT '粉币',
  `integral` INT UNSIGNED NOT NULL DEFAULT 0 COMMENT '积分 积分比例 按照商家定义',
  `order_coupons_id` INT NULL COMMENT 't_order_coupons.id 订单与优惠券的关联表',
  `receivable_price` INT UNSIGNED NOT NULL DEFAULT 0 COMMENT '应收金额 ： 所有消费账单和 房费(优惠后的价格) 一起累加',
  `remark` VARCHAR(200) NULL COMMENT '备注',
  `completion_time` DATETIME NULL COMMENT '总订单完成时间 (已完成状态) 完成总订单 必须把子订单全部都完成',
  `mark_modified` INT(1) NOT NULL DEFAULT 0 COMMENT '标记备注 0 启用 1 禁用 2 删除 默认0  可查看 sys_dictionary.dict_type_id=2',
  `created_by` INT NOT NULL COMMENT '创建者ID',
  `created_at` DATETIME NOT NULL COMMENT '创建时间',
  `updated_by` INT NOT NULL COMMENT '最后修改人 ID',
  `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP COMMENT '最后修改时间',
  PRIMARY KEY (`id`),
  UNIQUE INDEX `order_num_UNIQUE` (`order_num` ASC),
  INDEX `hotelId_busId_index` (`hotel_id` ASC, `bus_id` ASC))
ENGINE = InnoDB
COMMENT = '算客户实际 预约(到店办理)->入住->消费(酒店内消费)->退房->结算 的所有消费(房费+其他费)';


-- -----------------------------------------------------
-- Table `gt_hotel`.`t_order_room`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `gt_hotel`.`t_order_room` (
  `order_id` INT NOT NULL COMMENT '订单ID',
  `order_num` CHAR(25) NOT NULL COMMENT '订单编号 来自 t_order.order_num',
  `hotel_id` INT NOT NULL COMMENT '酒店ID',
  `hotel_name` VARCHAR(60) NULL COMMENT '冗余参数 酒店名称',
  `category_id` INT NOT NULL COMMENT '房型ID',
  `category_name` VARCHAR(45) NULL COMMENT '冗余参数 房型名称',
  `check_in_way` TINYINT(2) NOT NULL DEFAULT 0 COMMENT '入住方式 0 预订订单(预约订单: 网上|电话|移动端 预订过产生了一条预订消息则为预订订单)  1 到店入住(直接办理入住)',
  `room_num` CHAR(15) NULL COMMENT '房号 来自 t_room.number 预约订单则不会自动锁定房号',
  `customer_name` VARCHAR(45) NOT NULL COMMENT '预订客户姓名',
  `customer_id_type` TINYINT(1) NULL COMMENT '证件类型 =0 二代身份证 =1 一代身份证 =2 驾驶证 =3 护照 =4 军官证 =5 士兵证 =6 港澳通行证 =7 其他',
  `customer_id_card` CHAR(26) NULL COMMENT '证件号',
  `customer_id_card_image` VARCHAR(300) NULL COMMENT '证件照 图片地址',
  `customer_phone` CHAR(15) NOT NULL COMMENT '预订客户 手机号码',
  `number` TINYINT(3) NOT NULL DEFAULT 1 COMMENT '客房数量 默认1',
  `room_in_time` DATETIME NOT NULL COMMENT '抵店时间',
  `room_out_time` DATETIME NOT NULL COMMENT '离店时间',
  `rack_rate` INT UNSIGNED NOT NULL DEFAULT 0 COMMENT '当前门市价 固定从房型获取(周一至周四为 门市价 周五周六 为周末价。如果有日历价，则为日历价)',
  `receivable_price` INT UNSIGNED NOT NULL DEFAULT 0 COMMENT '应收( (客房价格+押金) * 数量 room_price+deposit ) ',
  `deposit` INT UNSIGNED NOT NULL DEFAULT 0 COMMENT '押金 来自 t_room_catrgory.deposit 0 则为免押金',
  `room_price` INT UNSIGNED NOT NULL DEFAULT 0 COMMENT '当前客房价格 可以是改价后的价格',
  `from` TINYINT(1) NOT NULL DEFAULT 0 COMMENT '来源 1 后台(线下订单) 2 ERP 3 移动端(H5) 4 小程序 ，默认 0 未知来源  ',
  `pay_time` DATETIME NULL COMMENT '支付时间',
  `pay_status` TINYINT(1) NOT NULL DEFAULT 1 COMMENT '支付状态 0 已支付 1 未支付',
  `activity_id` INT NULL COMMENT '活动ID，如果是活动进来的订单即可以使用活动ID',
  `completion_time` DATETIME NULL COMMENT '订单(退房)完成时间',
  `create_time` DATETIME NOT NULL COMMENT '创建订单时间',
  `mark_modified` INT(1) NOT NULL DEFAULT 0 COMMENT '标记备注 0 启用 1 禁用 2 删除 默认0  可查看 sys_dictionary.dict_type_id=2',
  `created_by` INT NOT NULL COMMENT '创建者ID',
  `created_at` DATETIME NOT NULL COMMENT '创建时间',
  `updated_by` INT NOT NULL COMMENT '最后修改人 ID',
  `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP COMMENT '最后修改时间',
  PRIMARY KEY (`order_id`),
  INDEX `orderId_hotelId_categoryId_index` (`order_id` ASC, `hotel_id` ASC, `category_id` ASC))
ENGINE = InnoDB
COMMENT = '酒店订单 客房订单 (客单)';


-- -----------------------------------------------------
-- Table `gt_hotel`.`t_hotel_member_setting`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `gt_hotel`.`t_hotel_member_setting` (
  `hotel_id` INT NOT NULL COMMENT '酒店ID',
  `vip_level` TINYINT(2) NOT NULL DEFAULT 1 COMMENT '会员等级 目前只有4个等级 1,2,3,4',
  `vip_check_out` DATETIME NULL COMMENT '会员 最晚退房时间(需要默认退房时间均为离店当天14点)',
  `free_deposit_enable` TINYINT(1) NOT NULL DEFAULT 1 COMMENT '是否免押金  0 是 1 否',
  PRIMARY KEY (`hotel_id`))
ENGINE = InnoDB
COMMENT = '酒店会员设置';


-- -----------------------------------------------------
-- Table `gt_hotel`.`t_room_permanent`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `gt_hotel`.`t_room_permanent` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `category_id` INT NOT NULL,
  `hotel_id` INT NOT NULL COMMENT '酒店ID t_hotel.id',
  `rule_name` VARCHAR(200) NOT NULL COMMENT '规则内容',
  `check_in_day` TINYINT(3) NOT NULL COMMENT '入住天数',
  `discount` INT NOT NULL DEFAULT 1 COMMENT '折扣 默认：1 无折扣(门市价/日历价 * 折扣) = 长包房当天价格',
  `deposit` INT NOT NULL COMMENT '押金',
  `mark_modified` TINYINT(1) NOT NULL DEFAULT 0 COMMENT '0 正常 1 禁用 2 删除',
  `created_by` INT NOT NULL COMMENT '创建者ID',
  `created_at` DATETIME NOT NULL COMMENT '创建时间',
  `updated_by` INT NOT NULL COMMENT '最后修改人 ID',
  `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP COMMENT '最后修改时间',
  PRIMARY KEY (`id`),
  INDEX `categoryId_hotelId_index` (`category_id` ASC, `hotel_id` ASC))
ENGINE = InnoDB
COMMENT = '客房长包房 长包房押金规则： 如 入住3天 押金按长包房押金收';


-- -----------------------------------------------------
-- Table `gt_hotel`.`t_authorization`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `gt_hotel`.`t_authorization` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `account_id` INT NOT NULL COMMENT '账号ID',
  `hotel_id` INT NOT NULL COMMENT '酒店ID',
  `function_ids` CHAR(20) NOT NULL COMMENT '功能ID 复数 存储方式：1,2,3,4 代表功能 =1 体现 =2 免押金 =3 修改房价 =4 挂账 来自 sys_dictionary.dict_type_id=3',
  `scan_code_authorization` TINYINT(1) NOT NULL DEFAULT 1 COMMENT '是否已扫码授权 0 是 1 否',
  `mark_modified` INT(1) NOT NULL DEFAULT 0 COMMENT '标记备注 0 启用 1 禁用 2 删除 默认0  可查看 sys_dictionary.dict_type_id=2',
  `created_by` INT NOT NULL COMMENT '创建者ID',
  `created_at` DATETIME NOT NULL COMMENT '创建时间',
  `updated_by` INT NOT NULL COMMENT '最后修改人 ID',
  `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP COMMENT '最后修改时间',
  PRIMARY KEY (`id`),
  INDEX `accountId_hotelId_index` (`account_id` ASC, `hotel_id` ASC))
ENGINE = InnoDB
COMMENT = 'ERP 功能授权管理';


-- -----------------------------------------------------
-- Table `gt_hotel`.`t_food`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `gt_hotel`.`t_food` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `hotel_id` INT NOT NULL,
  `name` VARCHAR(60) NOT NULL COMMENT '菜品名称',
  `price` INT NOT NULL COMMENT '单价',
  `delivery_time` INT NOT NULL COMMENT '配送时间 单位分钟',
  `food_provides` TINYINT(1) NOT NULL COMMENT '菜品提供方 0 本酒店 1 合作方',
  `food_provides_name` VARCHAR(60) NOT NULL COMMENT '提供方名称 food_provides =0 为酒店名称 =1 则填写合作方名称',
  `order_receive_phone` CHAR(15) NOT NULL COMMENT '新订单接收(短信)手机号',
  `food_image` VARCHAR(255) NULL COMMENT '菜品图片URL',
  `breakfast_enable` TINYINT(1) NOT NULL DEFAULT 1 COMMENT '早餐时段是否提供 0 提供 1 不提供',
  `breakfast_begin` TIME NULL COMMENT '早餐开始时间 (示例: 6:00-10:00)',
  `breakfast_end` TIME NULL COMMENT '早餐结束时间 (示例: 6:00-10:00)',
  `lunch_enable` TINYINT(1) NOT NULL DEFAULT 1 COMMENT '午餐时段是否提供 0 提供 1 不提供',
  `lunch_begin` TIME NULL COMMENT '午餐开始时间 (示例: 6:00-10:00)',
  `lunch_end` TIME NULL COMMENT '午餐结束时间 (示例: 6:00-10:00)',
  `dinner_enable` TINYINT(1) NOT NULL DEFAULT 1 COMMENT '晚餐是否提供 0 是 1 否',
  `dinner_begin` TIME NULL COMMENT '晚餐开始时间',
  `dinner_end` TIME NULL COMMENT '晚餐结束时间',
  `supper_enable` TINYINT(1) NOT NULL DEFAULT 1 COMMENT '宵夜是否提供 0 是 1 否',
  `supper_begin` TIME NULL COMMENT '宵夜开始时间',
  `supper_end` TIME NULL COMMENT '宵夜结束时间',
  `mark_modified` INT(1) NOT NULL DEFAULT 0 COMMENT '标记备注 0 启用 1 禁用 2 删除 默认0  可查看 sys_dictionary.dict_type_id=2',
  `created_by` INT NOT NULL COMMENT '创建者ID',
  `created_at` DATETIME NOT NULL COMMENT '创建时间',
  `updated_by` INT NOT NULL COMMENT '最后修改人 ID',
  `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP COMMENT '最后修改时间',
  PRIMARY KEY (`id`))
ENGINE = InnoDB
COMMENT = '菜品';


-- -----------------------------------------------------
-- Table `gt_hotel`.`t_order_room_customer`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `gt_hotel`.`t_order_room_customer` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `name` VARCHAR(60) NOT NULL COMMENT '入住客户姓名',
  `id_type` TINYINT(2) NOT NULL COMMENT '客户证件类型',
  `phone` CHAR(15) NULL COMMENT '手机号码',
  `id_card` CHAR(26) NOT NULL COMMENT '证件号',
  `id_card_image` VARCHAR(300) NULL COMMENT '扫描证件照',
  `order_id` INT NOT NULL COMMENT '订单ID',
  `room_num` CHAR(15) NOT NULL COMMENT '房号',
  `mark_modified` INT(1) NOT NULL DEFAULT 0 COMMENT '标记备注 0 启用 1 禁用 2 删除 默认0  可查看 sys_dictionary.dict_type_id=2',
  `created_by` INT NOT NULL COMMENT '创建者ID',
  `created_at` DATETIME NOT NULL COMMENT '创建时间',
  `updated_by` INT NOT NULL COMMENT '最后修改人 ID',
  `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP COMMENT '最后修改时间',
  PRIMARY KEY (`id`))
ENGINE = InnoDB
COMMENT = '登记入住客户 与 订单记录 一间客房 1 : n 入住客户身份信息(类似客户入住历史记录)';


-- -----------------------------------------------------
-- Table `gt_hotel`.`t_order_food`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `gt_hotel`.`t_order_food` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `order_id` INT NOT NULL COMMENT '总订单id',
  `order_num` CHAR(25) NOT NULL COMMENT '订单编号',
  `hotel_id` INT NOT NULL,
  `hotel_name` VARCHAR(60) NOT NULL COMMENT '酒店名称',
  `room_num` CHAR(15) NOT NULL,
  `category_id` INT NOT NULL,
  `category_name` VARCHAR(60) NULL COMMENT '房型名称',
  `customer_name` VARCHAR(45) NOT NULL COMMENT '客户姓名',
  `customer_phone` CHAR(15) NOT NULL COMMENT '客户联系电话',
  `order_status` TINYINT(2) NOT NULL DEFAULT 0 COMMENT '订单状态 =0 处理中 =1 已确认 =2 已取消 =3 已完成 默认0',
  `pay_status` TINYINT(2) NOT NULL DEFAULT 0 COMMENT '支付状态：=0 待支付 =1 已支付 =2 已退款',
  `refund_time` DATETIME NULL COMMENT '退款到账时间',
  `pay_time` DATETIME NULL COMMENT '支付时间',
  `create_time` DATETIME NOT NULL COMMENT '创建订单时间',
  `completion_time` DATETIME NULL COMMENT '订单完成时间( 确认订单已完成 )',
  `food_total_price` INT NOT NULL DEFAULT 0 COMMENT '订餐订单总价',
  `mark_modified` INT(1) NOT NULL DEFAULT 0 COMMENT '标记备注 0 启用 1 禁用 2 删除 默认0  可查看 sys_dictionary.dict_type_id=2',
  `created_by` INT NOT NULL COMMENT '创建者ID',
  `created_at` DATETIME NOT NULL COMMENT '创建时间',
  `updated_by` INT NOT NULL COMMENT '最后修改人 ID',
  `updated_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP COMMENT '最后修改时间',
  PRIMARY KEY (`id`))
ENGINE = InnoDB
COMMENT = '订餐订单 独立订单';


-- -----------------------------------------------------
-- Table `gt_hotel`.`t_order_food_detail`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `gt_hotel`.`t_order_food_detail` (
  `order_food_id` INT NOT NULL,
  `food_id` INT NOT NULL,
  `food_name` VARCHAR(60) NOT NULL COMMENT '菜名',
  `food_price` INT NOT NULL DEFAULT 0 COMMENT '单价',
  `food_provides_name` VARCHAR(60) NOT NULL COMMENT '菜品提供方',
  `food_provides_phone` CHAR(15) NOT NULL COMMENT '菜品提供方电话',
  `food_number` INT NOT NULL DEFAULT 1 COMMENT '数量',
  PRIMARY KEY (`order_food_id`))
ENGINE = InnoDB
COMMENT = '订餐订单详情';


-- -----------------------------------------------------
-- Table `gt_hotel`.`sys_operation_log`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `gt_hotel`.`sys_operation_log` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `reference_id` INT NOT NULL COMMENT 'module =HOTEL 则hotel_id =ROOM 则 room_id =ROOM_CATEGORY 则 房型ID =ORDER 则 order_id 等操作日志',
  `module` CHAR(20) NOT NULL,
  `action` VARCHAR(60) NOT NULL COMMENT '动作 module =ORDER 下的 改价功能 例如 客房状态需要记录',
  `modify_details` VARCHAR(200) NULL COMMENT '修改详情： 例如 订单下的改价功能 修改 原价格 288 -> 188 价格 ',
  `ip_address` VARCHAR(20) NOT NULL COMMENT 'ip 地址',
  `result` TINYINT(2) NOT NULL DEFAULT 1 COMMENT '处理结果 0 成功 1 失败',
  `operation_id` INT NOT NULL COMMENT '操作用户',
  `operatin_name` VARCHAR(60) NULL COMMENT '操作用户名',
  `create_time` DATETIME NULL COMMENT '创建时间',
  PRIMARY KEY (`id`),
  INDEX `rerId_module_index` (`reference_id` ASC, `module` ASC))
ENGINE = InnoDB
COMMENT = '系统操作日志';


-- -----------------------------------------------------
-- Table `gt_hotel`.`t_order_coupons`
-- -----------------------------------------------------
CREATE TABLE IF NOT EXISTS `gt_hotel`.`t_order_coupons` (
  `id` INT NOT NULL AUTO_INCREMENT,
  `order_id` INT NOT NULL,
  `order_num` CHAR(25) NOT NULL,
  `coupons_id` INT NOT NULL COMMENT '优惠券ID',
  `coupons_num` INT NOT NULL COMMENT '优惠券数量',
  PRIMARY KEY (`id`))
ENGINE = InnoDB
COMMENT = '订单与优惠券 1 : N ';


