mapper.select:
SELECT

FROM t_activity
LEFT JOIN t_activity_detail ON t_activity.id =
t_activity_detail.activity_id
WHERE mark_modified = 0

	AND id = #{param.id}


	AND hotel_id = #{param.hotelId}


	AND activity_type = #{param.activityType}


	AND publish_status = #{param.publishStatus}

ORDER BY t_activity.id DESC
