mapper.select:
SELECT a.id, c.`name`, b.book_name, b.book_phone, a.check_in_time, a.check_out_time, a.check_in_standard, a.price, a.quantity, a.order_status, a.pay_status, a.pay_type 
FROM t_erp_hotel_room_order a LEFT JOIN t_erp_hotel_room_order_guest b ON a.id = b.order_id 
LEFT JOIN t_erp_hotel c ON a.hotel_id = c.id 
WHERE b.guest_type = 0 

	AND a.bus_id = #{bus_id} 


	AND c.id = #{hotelId} 


	AND a.order_status = #{orderStatus} 


	AND a.pay_status = #{payStatus} 


	AND (b.book_name LIKE #{keyword} OR b.book_phone LIKE #{keyword})

mapper.update:
		UPDATE t_erp_hotel_room_order SET available = 0 
		WHERE id IN 

            #{item}
