mapper.select:
        SELECT
        , b.weekend_fare_enable weekendFareEnable, b.weekend_fare weekendFare 
        FROM t_room_calendar a 
		RIGHT JOIN t_room_category b ON a.category_id = b.id 
        
            
                AND a.id = #{param.id}
            
            
                AND b.id = #{categoryId}
            
            
                 AND abegin_time <= #{param.beginTime} 
            
            
                 AND aend_time >= #{param.endTime} 
            
        
        ORDER BY a.id DESC
