mapper.select:
SELECT hotel_id hotelId, id categoryId, name, room_count roomCount, rack_rate rackRate, deposit, weekend_fare_enable weekendFareEnable,
weekend_fare weekendFare
FROM t_room_category
WHERE mark_modified = 0

    AND id = #{queryRoomCategory.categoryId}


    AND hotel_id = #{queryRoomCategory.hotelId}

SELECT category.id, category.hotel_id hotelId, category.`name`, category.rack_rate rackRate, category.`desc`, category.deposit, category.weekend_fare_enable weekendFareEnable, category.weekend_fare weekendFare, category.breakfast_enable breakfastEnable, category.breakfast_quantity breakfastQuantity, category.room_count roomCount, setting.remnant_room_enable remnantRoomEnable, ifnull(calendar.price, 0) calenderPrice, IFNULL(SUM(o.room_order_num), 0) orderCount, # 房型 常量固定值 see CommonConstan 'ROOM_CATEGORY' AS module, (CASE category.weekend_fare_enable WHEN 0 THEN IFNULL(calendar.price, IF(DAYOFWEEK(#{param.roomInTime}) IN (6, 7), category.weekend_fare, category.rack_rate)) WHEN 1 THEN (IFNULL(calendar.price, category.rack_rate)) END) displayPrice FROM t_room_category category JOIN t_hotel_setting setting ON category.hotel_id = setting.hotel_id LEFT JOIN (SELECT category_id, price FROM t_room_calendar WHERE
#{param.roomInTime} >= begin_time AND #{param.roomInTime} <= end_time
) calendar ON category.id = calendar.category_id LEFT JOIN (SELECT tor.category_id, o.room_order_num, o.order_num FROM t_order o LEFT JOIN t_order_room tor ON o.order_num = tor.order_num WHERE # 所有的 到店支付 或 已支付 均确认过后的订单 则会扣减库存 o.hotel_id = #{hotelId} AND tor.room_in_time BETWEEN #{param.roomInTime} AND #{param.roomOutTime} AND ((o.pay_type = 2) OR (o.pay_status = 1) ) AND o.order_status IN (1, 4) AND o.mark_modified=0 GROUP BY o.order_num ) o ON category.id = o.category_id WHERE category.hotel_id = #{hotelId}
AND category.id = #{param.categoryId}
AND category.mark_modified = 0 GROUP BY category.id
HAVING orderCount < room_count
